import React from 'react'
import { hashHistory, Link } from 'react-router'
import server from '../libs/serverurls'
import ReactDOM from 'react-dom'
import QuizDetailSaved from './QuizDetailSaved.jsx';
import QuizResult from './QuizResult.jsx';

export default class Lecture extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			user: null,
			file: '',
			imagePreviewUrl: '',
			prev: "none",
			showDetailSaved:false,
			showResult:false
		}

		this.state.quiz = {
			subject: "",
			question: "",
			maxAnswer: "",
			time: "",
			multiple: false,
			correctAnswer: "",
			a: "",
			b: "",
			c: "",
			d: ""
		}

		this.state.saved = {
			subject: "",
			question: "",
			maxAnswer: "",
			time: "",
			multiple: false,
			correctAnswer: "",
			a: "",
			b: "",
			c: "",
			d: "",
			jpg: "",
			stat: false
		}

		this.state.sent = {
			subject: "",
			question: "",
			maxAnswer: "",
			time: "",
			multiple: false,
			correctAnswer: "",
			a: "",
			b: "",
			c: "",
			d: "",
			jpg: "",
			stat: false
		}
		this.state.result = {
			subject: "",
			multiple: false,
			correctAnswer: "",
			answer: {},
			answerImage: {},
			answerResult: {}
		}
		this.state.answer = [{
						  subject: "Multiple",
						  multiple: true,
						  answer: {
							  		bob: "A",
						  			charlie: "B",
						  			dave: "C",
						  			erin: "D"
						  		},
						  image: {}
						 },
						 {
						  subject: "Essay",
						  multiple: false,
						  answer: {
							  		bob: "Me",
						  			charlie: "You",
						  			dave: "He",
						  			erin: "She"
						  		},
						  image: {
						  			bob: "data:image/jpeg;base64,",
							  		charlie: "",
							  		dave: "data:image/jpeg;base64,",
							  		erin: ""
						  }
						 }
						]
    	this._handleImageChange = this._handleImageChange.bind(this);
  	}

	componentDidMount() {
		var container = ReactDOM.findDOMNode(this);

		this.canvas = ReactDOM.findDOMNode(this.refs.canvas);
		this.img = ReactDOM.findDOMNode(this.refs.img);
		this.canvasCtx = this.canvas.getContext('2d');
		this.canvas.width = 640;
		this.canvas.height = 640;
	}

	render() {
		var image_preview_input = {
			position: 'relative',
			overflow: 'hidden',
			margin: '0px',    
			color: '#333',
			backgroundColor: '#fff',
			borderColor: '#ccc'
		}
		var image_preview_input_title = {
			marginLeft: '2px'
			}
		var image_preview_input_file = {
			position: 'absolute',
			top: '0',
			right: '0',
			margin: '0',
			padding: '0',
			fontSize: '20px',
			cursor: 'pointer',
			opacity: '0',
			filter: 'alpha(opacity=0)'
		}
		
		return(				
		<div>	
			<h2>Quiz</h2>
			<table className="table">
			<tbody>
			<tr>
			<td rowSpan="2" className="col col-sm-6">
			<form >
				<label id="Label">Picture</label><br/>
				<canvas style={{width: "100%", height: "auto", display: this.state.prev}} ref="canvas" />
				<div style={{display: "none"}}>
				<img ref="img" src={this.state.imagePreviewUrl}/>
				</div>
				<span className="input-group-btn">
					<div className="btn btn-default" style={ image_preview_input }>
						<span style={ image_preview_input_title }><span className="glyphicon glyphicon-folder-open"></span> Browse</span>
						<input type="file" style={image_preview_input_file} onChange={this._handleImageChange} />
					</div>
				</span>
			<br/>
				<label id="Label">Subject</label>
				<input type="text" className="form-control" onChange={this.handleSubjectChange} value={this.state.quiz.subject}/>
			<br/>
				<label id="Label">Question</label>
				<textarea rows='5' className="form-control" onChange={this.handleQuestionChange} value={this.state.quiz.question}></textarea><br/>
				<span className="input-group-btn">
					<div className="btn btn-default" style={ image_preview_input } href="#mul" data-toggle="collapse" onClick={this.handleType}>
						<span style={ image_preview_input_title }><span className="glyphicon glyphicon-list"></span> Multiple Choice</span>
					</div>
				</span>
				<br />
				<div id="mul" className="collapse">
					<label id="Label">A. <input type="text" onChange={this.handleAnswerA} value={this.state.quiz.a}/><input type="radio" checked={this.state.quiz.correctAnswer === "A"} onClick={this.handleCorrectAnswerA}></input></label>
					<label id="Label">B. <input type="text" onChange={this.handleAnswerB} value={this.state.quiz.b}/><input type="radio" checked={this.state.quiz.correctAnswer === "B"} onClick={this.handleCorrectAnswerB}></input></label>
					<label id="Label">C. <input type="text" onChange={this.handleAnswerC} value={this.state.quiz.c}/><input type="radio" checked={this.state.quiz.correctAnswer === "C"} onClick={this.handleCorrectAnswerC}></input></label>
					<label id="Label">D. <input type="text" onChange={this.handleAnswerD} value={this.state.quiz.d}/><input type="radio" checked={this.state.quiz.correctAnswer === "D"} onClick={this.handleCorrectAnswerD}></input></label>
				</div>
				<br/>
				<label id="Label">Answer Limit<input type="text" className="form-control" onChange={this.handleMaxAnswer} value={this.state.quiz.maxAnswer}/></label><br/>
				<label id="Label">Time Limit<input type="text" className="form-control" onChange={this.handleTime} value={this.state.quiz.time}/></label>					  
				<button type="button" className="btn btn-primary btn-block" onClick={this.handleSaveQuiz} data-dismiss="modal">Save</button>
				<button type="button" className="btn btn-primary btn-block" onClick={this.handleClearQuiz} data-dismiss="modal">Clear</button>
			</form>
			</td>
			<td className="col col-sm-6">
				<div className="panel panel-danger">
				  <div className="panel-heading ">Saved Quiz</div>
				  <ul className="list-group">
					{ this.state.saved.stat ?
						<div>
							<li className="list-group-item panel-body">{this.state.saved.subject}
							<a className="pull-right" onClick={this.handleEditSaved} >Edit</a><br/>
							<a className="pull-right" onClick={this.handleDeleteQuizSaved}>Delete</a><br/>
							<a className="pull-right" onClick={this.handleSendQuizSaved}>Send</a>
							</li>
						</div>
					: null}
					
				  </ul>
				</div>
			</td>
			</tr>
			<tr>
			<td className="col col-sm-6">
				<div className="panel panel-success">
				  <div className="panel-heading ">Sent Quiz</div>
				  <ul className="list-group">
				  { this.state.sent.stat ?
						<div>
							<li className="list-group-item panel-body" >{this.state.sent.subject}
								<a className="pull-right" onClick={this.handleDetailSaved}>Detail</a><br/>
								<a className="pull-right" onClick={this.handleResult}>Result</a><br/>
								<a className="pull-right" onClick={this.handleDeleteQuizSent}>Remove</a><br/>
							</li>
						</div>
					: null}
				  </ul>
					{this.state.showDetailSaved ? <QuizDetailSaved 
						handleDetailSavedClose = {this.handleDetailSavedClose}
						quiz = {this.state.quiz}
						jpg = {this.state.sent.jpg}
					/> : null}
					{this.state.showResult ? <QuizResult 
						handleResultClose = {this.handleResultClose}
						result = {this.state.result}
					/> : null}
				</div>
			</td>
			</tr>
			</tbody>
			</table>
		</div>

		)
	}
	
	//===========================================================Input Hanlder
	handleSubjectChange = (event) => {
		var quiz = this.state.quiz;
		quiz.subject = event.target.value;
		this.setState(quiz);
		console.log('subject', event.target.value);
	}
	
	handleQuestionChange = (event) => {
		var quiz = this.state.quiz;
		quiz.question = event.target.value;
		this.setState(quiz);	
		console.log('question', event.target.value);
	}
	
	handleMaxAnswer = (event) => {
		var quiz = this.state.quiz;
		quiz.maxAnswer = event.target.value;
		this.setState(quiz);
		console.log('Max Answer ', event.target.value);
	}
	
	handleTime = (event) => {
		var quiz = this.state.quiz;
		quiz.time = event.target.value;
		this.setState(quiz);
		console.log('Time ', event.target.value);
	}
	
	handleAnswerA = (event) => {
		var quiz = this.state.quiz;
		quiz.a = event.target.value;
		this.setState(quiz);
		console.log('A ', event.target.value);
	}
	
	handleAnswerB = (event) => {
		var quiz = this.state.quiz;
		quiz.b = event.target.value;
		this.setState(quiz);
		console.log('B ', event.target.value);
	}
	
	handleAnswerC = (event) => {
		var quiz = this.state.quiz;
		quiz.c = event.target.value;
		this.setState(quiz);
		console.log('C ', event.target.value);
	}
	
	handleAnswerD = (event) => {
		var quiz = this.state.quiz;
		quiz.d = event.target.value;
		this.setState(quiz);
		console.log('D ', event.target.value);
	}
	
	handleCorrectAnswerA = (event) => {
		var quiz = this.state.quiz;
		quiz.correctAnswer = "A";
		this.setState(quiz);
		console.log('Correct Answer ', quiz);
	}
	
	handleCorrectAnswerB = (event) => {
		var quiz = this.state.quiz;
		quiz.correctAnswer = "B";
		this.setState(quiz);
		console.log('Correct Answer ', quiz);
	}
	
	handleCorrectAnswerC = (event) => {
		var quiz = this.state.quiz;
		quiz.correctAnswer = "C";
		this.setState(quiz);
		console.log('Correct Answer ', quiz);
	}
	
	handleCorrectAnswerD = (event) => {
		var quiz = this.state.quiz;
		quiz.correctAnswer = "D";
		this.setState(quiz);
		console.log('Correct Answer ', quiz);
	}
	
	handleType = (event) => {
		var quiz = this.state.quiz;
		if (this.state.quiz.multiple == false){
			quiz.multiple = true;
			}
		else {
			quiz.multiple = false;
		}
		this.setState(quiz);
		console.log('Type ', quiz.multiple);
	}
	
	//===========================================================Save Quiz Handler
	handleSaveQuiz = (event) => {
		if(this.state.prev != "none"){
			var jpg = this.canvas.toDataURL('image/jpeg', 0.6);
		}
		else{
			var jpg = "";
		}
		var quizData = {subject: this.state.quiz.subject,
			question: this.state.quiz.question,
			maxAnswer: this.state.quiz.maxAnswer,
			time: this.state.quiz.time,
			multiple: this.state.quiz.multiple,
			correctAnswer: this.state.quiz.correctAnswer,
			a: this.state.quiz.a,
			b: this.state.quiz.b,
			c: this.state.quiz.c,
			d: this.state.quiz.d,
			jpg: jpg,
			stat: true
		}
		this.setState({saved: quizData});
		console.log('saved state', quizData);
	}
	
	//===========================================================Send Quiz Handler
	handleSendQuizSaved = (event) => {
		var jpg = this.state.saved.jpg;
		var quizData = {subject: this.state.saved.subject,
			question: this.state.saved.question,
			maxAnswer: this.state.saved.maxAnswer,
			time: this.state.saved.time,
			multiple: this.state.saved.multiple,
			correctAnswer: this.state.saved.correctAnswer,
			a: this.state.saved.a,
			b: this.state.saved.b,
			c: this.state.saved.c,
			d: this.state.saved.d,
			jpg: jpg,
			stat: true
		}
		server.slideIO.emit('pushQuizQuestion', {quiz: quizData, jpg: jpg});
		console.log('quiz ', quizData, 'jpg', jpg);
		this.setState({sent: quizData});
		console.log('sent saved state', quizData);
	}
	
	//===========================================================Load Saved Quiz to Input Form
	handleEditSaved = (event) => {
		var load = this.state.saved;
		this.state.imagePreviewUrl = load.jpg;
		this.canvasCtx.drawImage(this.img, 0, 0, this.canvas.width, this.canvas.height);
		this.setState({quiz: load, prev: ""});
		console.log('load', load);
  }
	
	//===========================================================Open Saved Quiz Modal
	handleDetailSaved = (event) => {
	   	this.setState({showDetailSaved: true});
		console.log('show detail true');
  }
	
	//===========================================================Closa Saved Quiz Modal
	handleDetailSavedClose = (event) => {
       	this.setState({showDetailSaved: false});
		console.log('show detail false');
  }
	 
	//===========================================================Set State for Result && Show Result Modal
	handleResult = (event) => {
		var answerData={subject: "",
						multiple: false,
						correctAnswer: "",
						answer: {},
						answerImage: {},
						answerResult: {}
						};
		
		for(var i=0; i<this.state.answer.length; i++){
			if(this.state.sent.subject == this.state.answer[i].subject){
				answerData.subject = this.state.answer[i].subject;
				answerData.multiple = this.state.answer[i].multiple;
				answerData.answer = this.state.answer[i].answer;
				answerData.answerImage = this.state.answer[i].image;
				answerData.correctAnswer = this.state.sent.correctAnswer;
				
				for(var user in this.state.answer[i].answer){
					 if (this.state.answer[i].answer.hasOwnProperty(user)) {
						if(this.state.answer[i].answer[user] == answerData.correctAnswer){
							answerData.answerResult[user] = true;
						}
						else{
							answerData.answerResult[user] = false;
						}
					  }
				}
				this.setState({result: answerData});
				break;
			}
		}
		
	   	this.setState({showResult: true});
		console.log('show result true');
  	}
	
	//===========================================================Close Result Modal
	handleResultClose = (event) => {
       	this.setState({showResult: false});
		console.log('show result false');
  	}
	
	//===========================================================Delete Saved Quiz Data
	handleDeleteQuizSaved = (event) => {	
		var quizData = {subject: "",
			question: "",
			maxAnswer: "",
			time: "",
			multiple: false,
			correctAnswer: "",
			a: "",
			b: "",
			c: "",
			d: "",
			jpg: "",
			stat: false
		}
		this.setState({saved: quizData});
		console.log('delete state', this.state.saved);
	}
	
	//===========================================================Clear Input Form Quiz
	handleClearQuiz = (event) => {	
		var quizData = {subject: "",
			question: "",
			maxAnswer: "",
			time: "",
			multiple: false,
			correctAnswer: "",
			a: "",
			b: "",
			c: "",
			d: "",
			jpg: "",
			stat: false
		}
		this.canvasCtx.clearRect(0, 0, this.canvas.width, this.canvas.height);
		
		this.setState({quiz: quizData, prev: "none"});
		console.log('clear state', this.state.sent);
	}
	
	//===========================================================Delete Sent Quiz Data
	handleDeleteQuizSent = (event) => {	
		var quizData = {subject: "",
			question: "",
			maxAnswer: "",
			time: "",
			multiple: false,
			correctAnswer: "",
			a: "",
			b: "",
			c: "",
			d: "",
			jpg: "",
			stat: false
		}
		this.setState({sent: quizData});
		console.log('delete sent state', this.state.sent);
	}
	
	//===========================================================Canvas Handler
	_handleImageChange(e) {
    e.preventDefault();
	this.canvasCtx.clearRect(0, 0, this.canvas.width, this.canvas.height);
	this.canvas.width = 640;
	this.canvas.height = 640;
    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      this.setState({
        file: file,
        imagePreviewUrl: reader.result,
		prev: ""
      });
		console.log('image ', this.state);
		if(this.img.height < this.img.width){
			this.canvas.height = this.canvas.height*this.img.height/this.img.width;
			this.canvasCtx.drawImage(this.img, 0, 0, this.canvas.width, this.canvas.height);
			console.log('lanscape ', this.img);
		}
		else{
			this.canvas.width = this.canvas.width*this.img.width/this.img.height;
			//this.canvas.height = this.canvas.height*this.img.height/this.img.width;
			this.canvasCtx.drawImage(this.img, 0, 0, this.canvas.width, this.canvas.height);
			console.log('potrait ', this.img);
		}
    }
    reader.readAsDataURL(file)
  }
	 
}